import React from 'react';
import SortTag from './SortTag';
import {connect} from 'react-redux';

function SortNav(props) {

    const [show, updateShow] = React.useState(false);
    const [tags, updateTags] = React.useState([]);


    React.useEffect(() => {
        let check = false;
        let tagArray = [];

        for (let p in props.sorted) {
            if (props.sorted[p].toggle) {
                check = true;
                props.sorted[p].val.map((vl) => {
                    if(p==='health'){
                        tagArray.push(vl+"H");
                    }else if(p==='points'){
                        tagArray.push(vl+"P");
                    }else if(p==='cost'){
                        tagArray.push(vl+"C");
                    }else{
                        tagArray.push(vl);
                    }

                })
            }

        }

        updateShow(check);
        updateTags(tagArray);
    },
[props.sorted]);

let shower;

if (show) {
    shower = {
        top: '80px',
        backgroundColor: props.style.bodyText
    }
} else {
    shower = {
        top: '-80px',
        backgroundColor: props.style.bodyText
    }
}


let tagEles = tags.map((st, idx) =>
    <SortTag key={idx} val={st}/>
);

return (
    <div className={"sortNav"} style={shower}>
        {tagEles}
    </div>
)
}

const mapStateToProps = (state) => {
    return {
        sorted: state.sorted,
        style: state.style
    }
};

export default connect(mapStateToProps)(SortNav);
