import React from 'react';
import './less/style.css';
import List from './components/main/List';
import CardPage from './components/card/CardPage'
import Nav from './components/nav/Navigation';
import Options from './components/nav/options';

import {
    Switch,
    Route,  useLocation
} from "react-router-dom";



function App() {

    const [scroll, scrollTo]=React.useState(0);

    React.useEffect(() => {
        try {
            // trying to use new API - https://developer.mozilla.org/en-US/docs/Web/API/Window/scrollTo
            window.scroll({
                top: scroll,
                behavior: 'smooth'
            });
        } catch (error) {
            // just a fallback for older browsers
            window.scrollTo(0, 0);
        }
    }, []);


    let location = useLocation();


    let currentScroll = window.pageYOffset;

    let fixer;

    if (location.pathname!=="/") {
        fixer = {
            position: "fixed"
        };

        const wind = window.pageYOffset;

        scrollTo(wind);

        console.log(currentScroll);

    } else {
        fixer = {
            overflow: "visible"
        };

        // handleScrollTo(currentScroll);

    }




    return (

        <div className="App">
            <Nav/>
            <Options/>
            <div className={"mainWrapper"} style={fixer}>
                <Switch>
                    <Route path="/" component={List}/>
                </Switch>
            </div>
            <Route path={"/:id"} component={CardPage}/>
        </div>
    );
}


export default App;
