import React from 'react';
import {connect} from 'react-redux';

function SortNav(props) {

    const [show, updateShow] = React.useState(false);
    const [tags, updateTags] = React.useState([]);


    React.useEffect(() => {
        let check = false;
        let tagArray=[];

        for (let p in props.sorted) {
            if (props.sorted[p].toggle) {
                check = true;
                tagArray.push(p);
            }
        }

        updateShow(check);
        updateTags(tagArray);


    }, [props.sorted]);

    let shower;

    if(show){
       shower={
           top:'80px',
           backgroundColor:props.style.bodyText
       }
    }else{
        shower={
            top: '-80px',
            backgroundColor:props.style.bodyText
        }
    }

    let tagEles = tags.map((st, idx)=>
        <div key={idx} className={"itm"} style={{backgroundColor:props.style.body, color:props.style.nav}}>{st}</div>
    );


    return (
        <div className={"sortNav"} style={shower}>
            {tagEles}
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
        sorted: state.sorted,
        style:state.style
    }
};

export default connect(mapStateToProps)(SortNav);
