import React from 'react';
import './less/style.css';
import List from './components/main/List';
import CardPage from './components/card/CardPage'
import Nav from './components/nav/Navigation';
import Options from './components/nav/options';

import {
    Switch,
    Route, useLocation
} from "react-router-dom";

function App() {

    const [scroll, setScroll] = React.useState(window.pageYOffset);
    const [fix, setFix] = React.useState({
        overflow: "visible"
    })

    let location = useLocation();

    React.useEffect(()=>{

        const unsubscribe =()=>{
            if (location.pathname!=="/") {
                setFix({
                    position: "fixed"
                });

                setScroll(window.pageYOffset);

            } else {
                setFix({
                    overflow: "visible"
                });

                // document.body.scrollTop = scroll;
            }
        };

        unsubscribe();

        return ()=> unsubscribe();

    }, [scroll , fix, location.pathname]);







    return (

        <div className="App">
            <Nav/>
            <Options/>
            <div className={"mainWrapper"} style={fix}>
                <Switch>
                    <Route path="/" component={List}/>
                </Switch>


            </div>
            <Route path={"/:id"} component={CardPage}/>
        </div>
    );
}


export default App;
