import React from 'react';
import './less/style.css';
import List from './components/main/List';
import CardPage from './components/card/CardPage'
import Nav from './components/nav/Navigation';
import Options from './components/nav/options';

import {
    Switch,
    Route,  useLocation
} from "react-router-dom";



function App() {

    const [fix, setFix]=React.useState({
        overflow: "visible"
    });




    const resetScrollEffect = ({ element }) => {
        element.current.getScrollableNode().children[0].scrollTop = 0
    };

    let currentScroll = window.pageYOffset;

    let location = useLocation();

    React.useEffect(()=>{


        const unsubscribe =()=>{
            if (location.pathname!=="/") {
                setFix({
                    position: "fixed"
                });

                // currentScroll = window.pageYOffset;
                //
                // console.log(currentScroll);

            } else {
                setFix({
                    overflow: "visible"
                });

                // currentScroll = window.pageYOffset;
                //
                // console.log(currentScroll);

            }
        };

        unsubscribe();

        return ()=>unsubscribe();
    }, [fix]);


    return (

        <div className="App">
            <Nav/>
            <Options/>
            <div className={"mainWrapper"} style={fix}>
                <Switch>
                    <Route path="/" component={List}/>
                </Switch>


            </div>
            <Route path={"/:id"} component={CardPage}/>
        </div>
    );
}


export default App;
