import React from 'react';
import './less/style.css';
import List from './components/main/List';
import CardPage from './components/card/CardPage'
import Nav from './components/nav/Navigation';
import Options from './components/nav/options';

import {
    Switch,
    Route,  useLocation
} from "react-router-dom";



function App() {

    let location = useLocation();



    let currentScroll = window.pageYOffset;

    let fixer;

    if (location.pathname!=="/") {
        fixer = {
            position: "fixed"
        };

        currentScroll = window.pageYOffset;

        console.log(currentScroll);

    } else {
        fixer = {
            overflow: "visible"
        };

        // handleScrollTo(currentScroll);

    }

    const  [ele]= React.useState(<div className={"mainWrapper"} style={fixer} ref={el=>this.container=el}>
        <Switch>
            <Route path="/" component={List}/>
        </Switch>


    </div>);

    const handleScrollTo = (val) => {
        ele.scrollTop = val;
    };

    return (

        <div className="App">
            <Nav/>
            <Options/>
            {ele}
            <Route path={"/:id"} component={CardPage}/>
        </div>
    );
}


export default App;
