import React from 'react';

function Side(props){

    let finished = props.sd;

    if(props.sd.includes("RD")){
        finished = finished.replace("RD", " <span class='icon icon-ranged'></span>");
    }else if(props.sd.includes("Sh")){
        finished = finished.replace("Sh", " <span class='icon icon-shield'></span>");
    }else if(props.sd.includes("MD")){
        finished = finished.replace("MD", " <span class='icon icon-melee'></span>");
    }else if(props.sd.includes("ID")){
        finished = finished.replace("ID", " <span class='icon icon-indirect'></span>");
    }else if(props.sd.includes("Dc")){
        finished = finished.replace("Dc", " <span class='icon icon-discard'></span>");
    }else if(props.sd.includes("Dr")){
        finished = finished.replace("Dr", " <span class='icon icon-disrupt'></span>");
    }else if(props.sd.includes("Sp")){
        finished = finished.replace("Sp", " <span class='icon icon-special'></span>");
    }else if(props.sd.includes("R")){
        finished = finished.replace("R", " <span class='icon icon-resource'></span>");
    }

    if(!isNaN(props.sd[props.sd.length-1])&&props.sd[props.sd.length-1]!=="-"){
        finished = finished.replace(/.$/," "+props.sd[props.sd.length-1]+" <span class='res icon" +
            " icon-resource'></span>")
    }




    return(
        <div className={"side"} dangerouslySetInnerHTML={{ __html: finished }}></div>
    )
}

export default Side
