import React from 'react';

function Side(props){

    let finished = props.sd;

    if(props.sd.includes("RD")){
        finished = finished.replace("RD", "<span class='icon icon-ranged'></span>");
    }else if(props.sd.includes("Sh")){
        finished = finished.replace("RD", "<span class='icon icon-ranged'></span>");
    }else if(props.sd.includes("R")){
        finished = finished.replace("RD", "<span class='icon icon-resource'></span>");
    }else if(props.sd.includes("MD")){
        finished = finished.replace("RD", "<span class='icon icon-melee'></span>");
    }else if(props.sd.includes("ID")){
        finished = finished.replace("RD", "<span class='icon icon-indirect'></span>");
    }else if(props.sd.includes("Dc")){
        finished = finished.replace("RD", "<span class='icon icon-discard'></span>");
    }else if(props.sd.includes("Dr")){
        finished = finished.replace("RD", "<span class='icon icon-disrupt'></span>");
    }else if(props.sd.includes("Sp")){
        finished = finished.replace("RD", "<span class='icon icon-special'></span>");
    }




    return(
        <div className={"side"} dangerouslySetInnerHTML={{ __html: finished }}></div>
    )
}

export default Side
